function toggleTheme(){const html=document.documentElement,newTheme="dark"===html.getAttribute("data-theme")?"light":"dark";html.setAttribute("data-theme",newTheme),localStorage.setItem("theme",newTheme),updateProfilePicture(newTheme)}function updateProfilePicture(theme){const profileImg=document.querySelector(".profile-image-compact");profileImg&&(profileImg.src="dark"===theme?"public/images/dark_mode.png":"public/images/portfolio.png",profileImg.onerror=function(){this.src="https://ui-avatars.com/api/?name=Paul+Jorenz+Pablo&size=140&background=2563eb&color=fff&bold=true"})}function loadTheme(){const savedTheme=localStorage.getItem("theme")||"light";document.documentElement.setAttribute("data-theme",savedTheme),updateProfilePicture(savedTheme)}function sendEmail(){const yourEmail="jorenzlnu@gmail.com",subject=encodeURIComponent("Hello from your portfolio"),body=encodeURIComponent("Hi Paul,\n\nI visited your portfolio and would like to connect with you.\n\nBest regards,\n[Your Name]");if(/iPhone|iPad|iPod|Android/i.test(navigator.userAgent)){const gmailAppUrl=`googlegmail://co?to=${yourEmail}&subject=${subject}&body=${body}`;window.location.href=gmailAppUrl,setTimeout((function(){window.location.href=`mailto:${yourEmail}?subject=${subject}&body=${body}`}),500)}else{const gmailUrl=`https://mail.google.com/mail/?view=cm&fs=1&to=${yourEmail}&su=${subject}&body=${body}`;window.open(gmailUrl,"_blank","noopener,noreferrer")}}function initButtons(){const emailBtn=document.querySelector(".btn-primary");emailBtn&&emailBtn.addEventListener("click",(function(e){e.preventDefault(),sendEmail()}))}document.addEventListener("DOMContentLoaded",(function(){loadTheme(),initButtons()})),document.querySelectorAll('a[href^="#"]').forEach((anchor=>{anchor.addEventListener("click",(function(e){e.preventDefault();const target=document.querySelector(this.getAttribute("href"));target&&target.scrollIntoView({behavior:"smooth",block:"start"})}))}));